const SVG_colors={off:"#ccc",on:"#0d0",error:"#d00"};SVGDoc=[],SVGDoc.push(SVG_colors);class SVG_Text{constructor(id="text"){this.name=id,this.object=svg.getElementById(id),this.textObject=this.object.children[0],this.text=this.textObject.innerHTML,this.color=this.textObject.style.fill,SVGDoc.push(this)}setColor(color){anime({targets:this.textObject,fill:color}),this.color=color}setText(string){this.textObject.innerHTML=string,this.text=string}setOpacity(value){anime({targets:this.text,opacity:value})}}class SVG_Group{constructor(id="group"){this.name=id,this.object=svg.getElementById(id),this.state=!1,this.link=!1,SVGDoc.push(this)}when(evt,func){this.object.addEventListener(evt,func)}setOpacity(value){anime({targets:this.object,opacity:value})}setAsButton(y,y_compensation){this.when("mouseover",function(){console.log("mouseover"),anime({targets:this,translateY:-y})}),this.when("mouseleave",function(){console.log("mouseleave"),anime({targets:this,translateY:0})}),this.when("mousedown",function(){console.log("mousedown"),anime({targets:this,scaleY:y,translateY:y_compensation})}),this.when("mouseup",function(){console.log("mouseup"),anime({targets:this,scaleY:1,translateY:0})})}setLink(url){this.when("click",function(){window.location.href=url})}on(){this.state=!0,this.setOpacity(1)}off(){this.state=!0,this.setOpacity(.25)}}